import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Kitchen() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    axios.get('http://localhost:5000/orders')
      .then(response => setOrders(response.data))
      .catch(error => console.error('Error fetching orders:', error));
  };

  const handleCloseOrder = (orderId) => {
    axios.put(`http://localhost:5000/orders/${orderId}/close`)
      .then(response => {
        console.log('Order closed:', response.data);
        fetchOrders();  // Refresh orders after closing
      })
      .catch(error => {
        console.error('Error closing order:', error);
        alert('Failed to close order. Please try again.');
      });
  };

  return (
    <div>
      <h1>Kitchen Orders</h1>
      {orders.length > 0 ? (
        orders.map(order => (
          <div key={order.id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
            <h2>Table {order.table_id}</h2>
            <ul>
              {order.items.map((item, index) => (
                <li key={index}>{item.name}</li>
              ))}
            </ul>
            <button onClick={() => handleCloseOrder(order.id)}>Close Order</button>
          </div>
        ))
      ) : (
        <p>No open orders at the moment.</p>
      )}
    </div>
  );
}

export default Kitchen;
