import React, { useState } from 'react';
import axios from 'axios';
import Menu from './Menu';

const OrderForm = () => {
    const [tableId, setTableId] = useState('');
    const [selectedItems, setSelectedItems] = useState([]);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();

        if (!tableId || selectedItems.length === 0) {
            setError('Both table ID and items are required');
            return;
        }

        setError('');
        axios.post('http://localhost:5000/api/orders', {
            table_id: tableId,
            items: selectedItems.map(item => item.name)
        }).then(response => {
            console.log('Order submitted:', response.data);
            setTableId('');
            setSelectedItems([]);
            setSuccess('Order submitted successfully');
        }).catch(error => {
            console.error('Error submitting order:', error);
            setError('There was an error submitting the order. Please try again.');
        });
    };

    return (
        <div>
            <h1>Order Form</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Table ID:</label>
                    <input
                        type="text"
                        value={tableId}
                        onChange={(e) => setTableId(e.target.value)}
                    />
                </div>
                <Menu selectedItems={selectedItems} setSelectedItems={setSelectedItems} />
                <button type="submit">Submit Order</button>
            </form>
            <div>
                <h2>Selected Items</h2>
                <ul>
                    {selectedItems.map((item, index) => (
                        <li key={index}>{item.name}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default OrderForm;
