import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Kitchen() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/orders/table/1') // Example table ID 1
      .then(response => setOrders(response.data))
      .catch(error => console.error('Error fetching orders:', error));
  }, []);

  const handleCloseOrder = (orderId) => {
    axios.put(`http://localhost:5000/orders/${orderId}`)
      .then(response => {
        if (response.data.success) {
          setOrders(orders.filter(order => order.id !== orderId));
        }
      })
      .catch(error => console.error('Error closing order:', error));
  };

  return (
    <div>
      <h1>Kitchen</h1>
      {orders.map(order => (
        <div key={order.id}>
          <h2>Table {order.table_id}</h2>
          <div>
            {JSON.parse(order.items).map((item, index) => (
              <div key={index}>{item.name}</div>
            ))}
          </div>
          <button onClick={() => handleCloseOrder(order.id)}>Close Order</button>
        </div>
      ))}
    </div>
  );
}

export default Kitchen;
