import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Billing() {
  const { orderId } = useParams();  // Get the order ID from the URL params
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);

  // Fetch order details from the backend
  useEffect(() => {
    axios.get(`http://localhost:5000/orders/${orderId}`)
      .then(response => {
        setOrder(response.data);
      })
      .catch(error => {
        console.error('Error fetching order data:', error);
      });
  }, [orderId]);

  const handlePrint = () => {
    window.print(); // This will trigger the browser's print dialog
  };

  const handleProcessBill = async () => {
    try {
      // Call the backend to process the bill
      const response = await axios.post('http://localhost:5000/billing', { orderId });
      console.log('Bill processed:', response.data);
      // Optionally, you can display the processed total here

      // Redirect to a different page or show a success message
      alert('Bill processed successfully!');
      navigate(`/billing/${orderId}`);
    } catch (error) {
      console.error('Error processing bill:', error);
      alert('Failed to process bill.');
    }
  };

  if (!order) return <p>Loading...</p>;

  return (
    <div className="billing-page">
      <h1>Bill for Table {order.table_id}</h1>
      <div className="bill-details">
        <ul>
          {order.items && order.items.map((item, index) => (
            <li key={index}>
              {item.name} - Quantity: {item.quantity} - Price: ${item.price}
            </li>
          ))}
        </ul>
        <p>Total: ${order.totalAmount}</p>
      </div>
      <button onClick={handlePrint}>Print Bill</button>
      <button onClick={handleProcessBill}>Process Bill</button>
    </div>
  );
}

export default Billing;
