// Reservation.js
import React, { useState } from 'react';
import './Reservation.css'; // Add your custom styles for the page

function Reservation() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [people, setPeople] = useState(1);
  const [message, setMessage] = useState('');

  const handleReservationSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the reservation data to your backend

    setMessage('Reservation Successful! We look forward to hosting you.');
  };

  return (
    <div className="reservation-container">
      <h1>Book a Table</h1>
      <form className="reservation-form" onSubmit={handleReservationSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Time</label>
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Number of People</label>
          <input
            type="number"
            value={people}
            onChange={(e) => setPeople(e.target.value)}
            min="1"
            required
          />
        </div>
        <button type="submit" className="submit-button">Reserve</button>
      </form>

      {message && <p className="reservation-message">{message}</p>}
    </div>
  );
}

export default Reservation;
