import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Menu = ({ selectedItems, setSelectedItems }) => {
    const [menu, setMenu] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        // Replace this with an actual API call to fetch menu items if available
        const fetchMenu = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/menu'); // Ensure you have this endpoint
                setMenu(response.data);
            } catch (error) {
                console.error('Error fetching menu:', error);
                setError('There was an error fetching the menu. Please try again.');
            }
        };
        fetchMenu();
    }, []);

    const handleSelectItem = (item) => {
        setSelectedItems([...selectedItems, item]);
    };

    return (
        <div>
            <h2>Menu</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {menu.map(item => (
                    <li key={item.id}>
                        <p>{item.name} - ${item.price}</p>
                        <button onClick={() => handleSelectItem(item)}>Add to Order</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Menu;
