// Reservations.js
import React, { useState } from 'react';

import axios from 'axios';
import './Reservation.css'; // Add your custom styles for the page

function Reservation() {


  const [tableId, setTableId] = useState('');
  const [customerName, setCustomerName] = useState('');
  const [reservationTime, setReservationTime] = useState('');
  const [message, setMessage] = useState('');

  const handleReserve = async () => {
    try {
      const response = await axios.post('http://localhost:5000/reservations/reserve', {
        tableId,
        customerName,
        reservationTime,
      });
      setMessage(response.data);
    } catch (error) {
      const errorMessage = error.response?.data || 'Error reserving the table';
      setMessage(errorMessage); }
  };

  return (
    <div>
      <h1>Reserve Your Table</h1>
      <form onSubmit={(e) => e.preventDefault()}>
        <div>
          <label>Customer Name: </label>
          <input
            type="text"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Table Number: </label>
          <input
            type="number"
            value={tableId}
            onChange={(e) => setTableId(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Reservation Time: </label>
          <input
            type="datetime-local"
            value={reservationTime}
            onChange={(e) => setReservationTime(e.target.value)}
            required
          />
        </div>
        <button onClick={handleReserve}>Reserve Table</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}



export default Reservation;
