import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function Menu() {
  const { tableId } = useParams();
  const [menuItems, setMenuItems] = useState([]);
  const [order, setOrder] = useState([]);
  const [orderSubmitted, setOrderSubmitted] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/menu')
      .then(response => setMenuItems(response.data))
      .catch(error => console.error('Error fetching menu items:', error));
  }, []);

  const handleAddItem = (item) => {
    setOrder([...order, item]);
  };

  const handleRemoveItem = (index) => {
    const newOrder = [...order];
    newOrder.splice(index, 1);
    setOrder(newOrder);
  };

  const handleSubmitOrder = () => {
    axios.post('http://localhost:5000/orders', { table_id: tableId, items: order })
      .then(response => {
        console.log('Order submitted:', response.data);
        setOrderSubmitted(true);
      })
      .catch(error => {
        console.error('Error submitting order:', error);
        alert('Failed to submit order. Please try again.');
      });
  };

  const handleTakeAnotherOrder = () => {
    setOrder([]);
    setOrderSubmitted(false);
    navigate('/');
  };

  return (
    <div>
      <h1>Menu for Table {tableId}</h1>
      {!orderSubmitted ? (
        <>
          <div>
            {menuItems.map(item => (
              <div key={item.id}>
                <span>{item.name}</span>
                <button onClick={() => handleAddItem(item)}>Add</button>
              </div>
            ))}
          </div>
          <div>
            <h2>Order</h2>
            {order.map((item, index) => (
              <div key={index}>
                <span>{item.name}</span>
                <button onClick={() => handleRemoveItem(index)}>Remove</button>
              </div>
            ))}
            <button onClick={handleSubmitOrder}>Submit Order</button>
          </div>
        </>
      ) : (
        <div>
          <h2>Order Submitted Successfully!</h2>
          <p>The food is being prepared.</p>
          <button onClick={handleTakeAnotherOrder}>Take Another Order</button>
        </div>
      )}
    </div>
  );
}

export default Menu;
