import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OrderList = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/orders')
            .then(response => setOrders(response.data))
            .catch(error => console.error(error));
    }, []);

    const handleDelivered = id => {
        axios.put(`http://localhost:5000/api/orders/${id}`)
            .then(response => setOrders(orders.filter(order => order.id !== id)))
            .catch(error => console.error(error));
    };

    return (
        <div>
            <h2>Orders</h2>
            <ul>
                {orders.map(order => (
                    <li key={order.id}>
                        Table {order.table_id} - {JSON.parse(order.items).join(', ')}
                        <button onClick={() => handleDelivered(order.id)}>Delivered</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default OrderList;
