import React, { useState } from 'react';
import axios from 'axios';
import './Reservation.css'; // Import external CSS

function Reservation() {
  const [tableId, setTableId] = useState('');
  const [customerName, setCustomerName] = useState('');
  const [reservationTime, setReservationTime] = useState('');
  const [message, setMessage] = useState('');

  const handleReserve = async () => {
    try {
      const response = await axios.post('http://localhost:5000/reservations/reserve', {
        tableId,
        customerName,
        reservationTime,
      });
      setMessage(response.data);
    } catch (error) {
      const errorMessage = error.response?.data || 'Error reserving the table';
      setMessage(errorMessage);
    }
  };

  return (
    <div className="reservation-container">
      <h1 className="reservation-title">Reserve Your Table</h1>
      <div className="reservation-card">
        <form
          onSubmit={(e) => e.preventDefault()}
          className="reservation-form"
        >
          <div className="form-group">
            <label htmlFor="customerName" className="form-label">
              Customer Name:
            </label>
            <input
              type="text"
              id="customerName"
              className="form-control"
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="tableId" className="form-label">
              Table Number:
            </label>
            <input
              type="number"
              id="tableId"
              className="form-control"
              value={tableId}
              onChange={(e) => setTableId(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="reservationTime" className="form-label">
              Reservation Time:
            </label>
            <input
              type="datetime-local"
              id="reservationTime"
              className="form-control"
              value={reservationTime}
              onChange={(e) => setReservationTime(e.target.value)}
              required
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary"
            onClick={handleReserve}
          >
            Reserve Table
          </button>
        </form>
        {message && (
          <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
}

export default Reservation;
