import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function Menu() {
  const { tableId } = useParams();
  const [menuItems, setMenuItems] = useState([]);
  const [order, setOrder] = useState({});
  const [orderSubmitted, setOrderSubmitted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/menu')
      .then(response => {
        setMenuItems(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching menu items:', error);
        setError('Failed to load menu. Please try again.');
        setLoading(false);
      });
  }, []);

  const handleAddItem = (item) => {
    setOrder(prevOrder => {
      const newOrder = { ...prevOrder };
      if (newOrder[item.id]) {
        newOrder[item.id].quantity += 1;
      } else {
        newOrder[item.id] = { ...item, quantity: 1 };
      }
      return newOrder;
    });
  };

  const handleRemoveItem = (itemId) => {
    setOrder(prevOrder => {
      const newOrder = { ...prevOrder };
      if (newOrder[itemId]) {
        newOrder[itemId].quantity -= 1;
        if (newOrder[itemId].quantity === 0) {
          delete newOrder[itemId];
        }
      }
      return newOrder;
    });
  };

  const handleSubmitOrder = () => {
    const orderItems = Object.values(order);
    if (orderItems.length === 0) {
      alert('No items in the order.');
      return;
    }

    axios.post('http://localhost:5000/orders', { table_id: tableId, items: orderItems })
      .then(response => {
        console.log('Order submitted:', response.data);
        setOrderSubmitted(true);
      })
      .catch(error => {
        console.error('Error submitting order:', error);
        alert('Failed to submit order. Please try again.');
      });
  };

  const handleTakeAnotherOrder = () => {
    setOrder({});
    setOrderSubmitted(false);
    navigate('/');
  };

  if (loading) {
    return <p>Loading menu...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h1>Menu for Table {tableId}</h1>
      {!orderSubmitted ? (
        <>
          <div>
            {menuItems.map(item => (
              <div key={item.id}>
                <span>{item.name}</span>
                <button onClick={() => handleAddItem(item)}>Add</button>
              </div>
            ))}
          </div>
          <div>
            <h2>Order</h2>
            {Object.values(order).map(item => (
              <div key={item.id}>
                <span>{item.name} (x{item.quantity})</span>
                <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
              </div>
            ))}
            <button onClick={handleSubmitOrder}>Submit Order</button>
          </div>
        </>
      ) : (
        <div>
          <h2>Order Submitted Successfully!</h2>
          <p>The food is being prepared.</p>
          <button onClick={handleTakeAnotherOrder}>Take Another Order</button>
        </div>
      )}
    </div>
  );
}

export default Menu;
