import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Kitchen() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    axios.get('http://localhost:5000/orders')
      .then(response => setOrders(response.data))
      .catch(error => console.error('Error fetching orders:', error));
  };

  const handleCloseOrder = (tableId) => {
    axios.put(`http://localhost:5000/orders/${orderId}/close`)
      .then(response => {
        console.log('Order closed:', response.data);
        fetchOrders();  // Refresh orders after closing
      })
      .catch(error => {
        console.error('Error closing order:', error);
        alert('Failed to close order. Please try again.');
      });
  };

  return (
    <div className="kitchen-container">
      <h1 className="page-title">Kitchen Orders</h1>
      <div className="orders-list">
        {orders.map(order => (
          <div key={order.table_id} className="order-card">
            <h2 className="order-table">Table {order.table_id}</h2>

            {/* Add check to make sure order.items is an array and not null */}
            <ul className="order-items">
              {order.items && Array.isArray(order.items) && order.items.length > 0 ? (
                order.items.map((item, index) => (
                  // Ensure item and item.name exist before rendering
                  item && item.name ? (
                    <li key={index} className="order-item">{item.name}</li>
                  ) : (
                    <li key={index} className="order-item">Unnamed item</li>
                  )
                ))
              ) : (
                <li className="order-item">No items for this table</li>
              )}
            </ul>

            <button className="close-button" onClick={() => handleCloseOrder(order.table_id)}>Close Order</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Kitchen;
