import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
function Tables() {
  const [tables, setTables] = useState([]); // All tables
  const [reservations, setReservations] = useState([]); // Reservations data
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  // Fetch all tables and reservations
  useEffect(() => {
    const fetchTables = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tables');
        setTables(response.data);
      } catch (error) {
        console.error('Error fetching tables:', error);
      }
    };
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:5000/orders?status=Pending');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };
  
    
  
    const fetchReservations = async () => {
      try {
        const response = await axios.get('http://localhost:5000/reservations');
        setReservations(response.data);
      } catch (error) {
        console.error('Error fetching reservations:', error);
      }
    };

    fetchTables();
    fetchOrders();
    fetchReservations();
  }, []);
  const markAsDelivered = async (orderId) => {
    try {
      await axios.put(`http://localhost:5000/orders/${orderId}/status`, {
        status: 'Delivered',
      });
      alert('Order marked as Delivered.');
      // Update orders in the frontend
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order.id === orderId ? { ...order, status: 'Delivered' } : order
        )
      );
    } catch (error) {
      console.error('Error marking order as Delivered:', error);
    }
  };
  
  const processBill = async (orderId) => {
    try {
      const response = await axios.post(`http://localhost:5000/billing`, { orderId });
      alert('Bill processed successfully.');
    } catch (error) {
      console.error('Error processing bill:', error);
    }
  };
  
  // Check if a table is reserved
  const isTableReserved = (tableId) => {
    const currentTime = new Date();
    return reservations.some(
      (reservation) =>
        reservation.table_id === tableId &&
        new Date(reservation.reservation_time) >= currentTime // Reserved for now or future
    );
  };

  // Navigate to the menu page for available tables
  const handleTableClick = (tableId) => {
    if (!isTableReserved(tableId)) {
      navigate(`/menu/${tableId}`);
    }
  };

  return (
    <div className="tables-with-reservations">
      {/* Tables Section */}
      <h1 className="page-title">Tables</h1>
      <div className="tables-grid">
        {tables.map((table) => {
          const reserved = isTableReserved(table.id);
          return (
            <div key={table.id} className={`table-container ${reserved ? 'reserved' : ''}`}>
              <img
                src={`/images/table1.jpg`}
                alt={`Table ${table.id}`}
                className={`table-image ${reserved ? 'table-disabled' : ''}`}
                onClick={() => handleTableClick(table.id)}
              />
              <p className="table-label">
                {reserved ? `Table ${table.id} - Reserved` : `Table ${table.id}`}
              </p>
            </div>
          );
        })}
      </div>
      <div className="orders-list">
  <h2>Orders Pending Action</h2>
  <table>
    <thead>
      <tr>
        <th>Order ID</th>
        <th>Table Number</th>
        <th>Status</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      {orders.map((order) => (
        <tr key={order.id}>
          <td>{order.id}</td>
          <td>{order.table_id}</td>
          <td>{order.status}</td>
          <td>
            <button onClick={() => markAsDelivered(order.id)}>Mark as Delivered</button>
            <button onClick={() => processBill(order.id)}>Process Bill</button>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
</div>

      {/* Reservations Section */}
      <div className="reservations-list">
        <h2>Today's Reservations</h2>
        <table>
          <thead>
            <tr>
              <th>Table Number</th>
              <th>Customer Name</th>
              <th>Reservation Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {reservations.map((reservation) => (
              <tr key={reservation.id}>
                <td>{reservation.table_id}</td>
                <td>{reservation.customer_name}</td>
                <td>{new Date(reservation.reservation_time).toLocaleString()}</td>
                <td>{reservation.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Tables;
